                  </Box>
                </Flex>
                <Box>
                  <BookingForm
                    roomPrice={details?.roomPrice}
                    waterPrice={details?.waterPrice}
                    electricityPrice={details?.electricityPrice}
                  />
                </Box>
              </Box>
            </Flex>
          </Box>
        </Container>
      </Box>
    </Box>
  )
}

