import { createReducer } from '@reduxjs/toolkit'
import actions from '../actions'

const { authSuccess, signOut } = actions

const initialState = {
  isAuth: false,
  redirectTo: '/'
}

const auth = createReducer(initialState, (builder) => {
  builder
    .addCase(authSuccess, state => {
      return { ...state, isAuth: true }
    })
  // [authSuccess.type]: (state) => {
  //   return { ...state, isAuth: true }
  // },
  // [signOut.type]: () => {
  //   return { isAuth: false, redirectTo: '' }
  // },
})

export default auth
