import { createAction } from '@reduxjs/toolkit';
var loginByEmailAndPassword = createAction('LOG_IN_BY_EMAIL_PASSWORD');
var saveRedirectTo = createAction('SAVE_REDIRECT_TO');
var signupByEmailAndPassword = createAction('SIGN_UP_BY_EMAIL_PASSWORD');
var authSuccess = createAction('AUTH_SUCCESS');
var signOut = createAction('SIGN_OUT');
var authActions = {
  loginByEmailAndPassword: loginByEmailAndPassword,
  signupByEmailAndPassword: signupByEmailAndPassword,
  saveRedirectTo: saveRedirectTo,
  authSuccess: authSuccess,
  signOut: signOut
};
export default authActions;
