import { Box, Button, Flex, useToast } from '@chakra-ui/react'
import { useState } from 'react'
import axios from 'utils/axios'
import { useParams } from 'react-router-dom'

type Params = {
  room_id: string
}
const Actions = (props: any) => {
  const { isPay } = props
  const toast = useToast()
  const params: Params = useParams()

  const [payFlag, setPayFlag] = useState(isPay)
  const handleAccept = (id: any) => {
    axios
      .put(`owner/rooms/${params?.room_id}/rent`)
      .then((res) => {
        setPayFlag(true)
        toast({
          title: 'Thành công',
          description: 'Bạn đã cho thuê thành công',
          status: 'success',
          duration: 3000,
          isClosable: true,
          position: 'top',
        })
      })
      .catch((err) => {
        console.log(err)
        toast({
          title: 'Có sự cố xảy ra',
          description: 'Bạn không đủ quyền để truy cập trang này',
          status: 'error',
          duration: 3000,
          isClosable: true,
          position: 'top',
        })
      })
  }

  const removeRent = (id: any) => {
    axios
      .delete(`owner/rooms/${params?.room_id}/rent`)
      .then((res) => {
        setPayFlag(true)
        toast({
          title: 'Thành công',
          description: 'Bạn đã cho thuê thành công',
          status: 'success',
          duration: 3000,
          isClosable: true,
          position: 'top',
        })
      })
      .catch((err) => {
        console.log(err)
        toast({
          title: 'Có sự cố xảy ra',
          description: 'Bạn không đủ quyền để truy cập trang này',
          status: 'error',
          duration: 3000,
          isClosable: true,
          position: 'top',
        })
      })
  }

  return (
    <Box padding='1.5rem 0'>
      <Flex flexDirection='row' justifyContent='flex-end'>
        {!payFlag && (
          <>
            <Button
              outline='0'
              onClick={handleAccept}
              fontSize='1.25rem'
              colorScheme='green'
              mr='10px'
              fontWeight='500'>
              Cho Thuê
            </Button>
            <Button
              outline='0'
              onClick={removeRent}
              fontSize='1.25rem'
              colorScheme='red'
              mr='10px'
              fontWeight='500'>
              Từ chối
            </Button>
          </>
        )}

        {
          props.status === 'REJECTED' && (
              <Button
                outline='0'
                onClick={removeRent}
                fontSize='1.25rem'
                colorScheme='red'
                mr='10px'
                fontWeight='500'>
              Xóa phòng
              </Button>
            )
        }
      </Flex>
    </Box>
  )
}

export default Actions
